import { Dimensions } from 'react-native';

const SCREEN_WIDTH: number = Dimensions.get('window').width;
const FACTOR = 414;

/**
 * 414为屏幕基准适配
 * @param v: value
 */
export default function rem(v: number): number {
    return Number(((SCREEN_WIDTH / FACTOR) * v).toFixed(1));
}

export function remWith(baseWidth: number, v: number) {
    return Number(((baseWidth / FACTOR) * v).toFixed(1));
}
