/**
 * 日期格式转换
 * @param {Int|String} date  // 日期，如：'2022-07-12 10:10:10'
 * @param {String} fmt       // 日期转换格式，如: 'YYYY-MM-DD HH:mm:ss'
 * @returns {String}
 */
exports.dateFormat = (date, fmt) => {
  const currentDate = new Date(date);
  let ret;
  const opt = {
    'y+': currentDate.getFullYear().toString(), // 年
    'Y+': currentDate.getFullYear().toString(), // 年
    'M+': (currentDate.getMonth() + 1).toString(), // 月
    'd+': currentDate.getDate().toString(), // 日
    'D+': currentDate.getDate().toString(), // 日
    'h+': currentDate.getHours().toString(), // 时
    'H+': currentDate.getHours().toString(), // 时
    'm+': currentDate.getMinutes().toString(), // 分
    's+': currentDate.getSeconds().toString(), // 秒
    // 有其他格式化字符需求可以继续添加，必须转化成字符串
  };
  for (let k in opt) {
    ret = new RegExp('(' + k + ')').exec(fmt);
    if (ret) {
      fmt = fmt.replace(ret[1], ret[1].length == 1 ? opt[k] : opt[k].padStart(ret[1].length, '0'));
    }
  }
  return fmt;
};
