export interface BridgeErrorContext {
    bridge: string;
    params: unknown;
}

export class BridgeError extends Error {
    name = 'BridgeError';

    code: number;

    context: BridgeErrorContext;

    constructor(message: string, code: number, context: BridgeErrorContext) {
        super(message);

        this.message = message;
        this.code = code;
        this.context = context;
    }
}

export const isBridgeError = (err: unknown): err is BridgeError =>
    err instanceof BridgeError;
