export const invalidResultBridges = ['social.currentUserInfo'] as const;

export const multiCallbackBridges = [
    'component.download',
    'system.downloadThirdPartyAPP',
    'post.postVideo',
    'post.uploadShopVideo',
    'post.reUploadShopVideo',
    'post.selectImage',
    'post.karaoke',
    'pay.startHalfScreenRecharge',
] as const;

export const multiCallbackBridgeSet = new Set<string>(multiCallbackBridges);

export const callbackOnlyBridgeSet = new Set<string>([
    ...invalidResultBridges,
    ...multiCallbackBridges,
]);

const krnAppStateEvents = ['krnAppStateDidChange', 'kdsMemoryWarning'] as const;

export const krnAppStateEventSet = new Set<string>(krnAppStateEvents);

export type KRNAppStateEvents = typeof krnAppStateEvents[number];

export type CallbackOnlyBridge =
    | typeof invalidResultBridges[number]
    | typeof multiCallbackBridges[number];
