import { NativeModules, Platform } from 'react-native';

export default async (params: any): Promise<any> => {
    return new Promise((resolve, reject) => {
        try {
            const result = NativeModules.KRNBasic.getSlideBack(
                Platform.OS === 'android' ? params : params.rootTag,
            );
            resolve({
                enabled: !!result,
            });
        } catch (err) {
            reject({ code: -1, message: `failed to getSlideBack: ${err}` });
        }
    });
};
