package com.kwai.kds.image


import android.view.View
import com.facebook.drawee.controller.ControllerListener
import com.facebook.react.views.imagehelper.ImageSource

object KwaiImageManagerConfig {
  var callerContextFactory: KwaiImageCallerContextFactory? = null

  var controllerListenerFactory: KwaiImageControllerListenerFactory? = null
}

interface KwaiImageCallerContextFactory {
  // 有可能仅仅只是请求图片，view是null
  fun createCallerContext(view: View?, imageSource: ImageSource): Any?
}

interface KwaiImageControllerListenerFactory {
  fun createControllerListenerFactory(): ControllerListener<*>?
}

