package com.kwai.kds.image;

import com.facebook.react.bridge.Promise;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.bridge.ReadableArray;
import com.facebook.react.bridge.ReadableMap;
import com.facebook.react.module.annotations.ReactModule;
import com.facebook.react.modules.image.ImageLoaderModule;

@ReactModule(name = KwaiImageModule.NAME)
public class KwaiImageModule extends ReactContextBaseJavaModule {

  public static final String NAME = "KwaiImageView";

  private ImageLoaderModule mDelegateModule;

  public KwaiImageModule(ReactApplicationContext reactApplicationContext) {
    super(reactApplicationContext);

    mDelegateModule = new ImageLoaderModule(reactApplicationContext);
  }

  @Override
  public String getName() {
    return NAME;
  }

  @ReactMethod
  public void preload(ReadableArray sources, Promise promise) {
    PromisePrefetch promisePrefetch = new PromisePrefetch(sources.size(), promise);
    for (int i = 0; i < sources.size(); i++) {
      ReadableMap source = sources.getMap(i);
      String uri = source.getString("uri");

      // FIXME: requestId?
      mDelegateModule.prefetchImage(uri, uri.hashCode(), promisePrefetch);
    }
  }

  @ReactMethod
  public void getSize(String url, Promise promise) {
     mDelegateModule.getSize(url, promise);
  }
}
