import React, { useState, useEffect } from 'react';
import { UriProps, fetchText, err } from '../xml/common';
import SvgCss from './SvgCss';


export function SvgCssUri(props: UriProps) {
    const { uri } = props;
    const [xml, setXml] = useState<string | null>(null);
    useEffect(() => {
        uri
            ? fetchText(uri)
                .then(setXml)
                .catch(err)
            : setXml(null);
    }, [uri]);
    return <SvgCss xml={xml} override={props} />;
}

export default SvgCssUri;