import { requireNativeComponent as rnc, HostComponent } from 'react-native';

const SVGNameMap: Map<string, HostComponent<unknown>> = new Map<string, HostComponent<unknown>>();
const getRNC = (name: string): HostComponent<unknown> => {
    if(SVGNameMap.has(name)) {
        return SVGNameMap.get(name)!;
    } else {
        SVGNameMap.set(name, rnc(name));
        return SVGNameMap.get(name)!;
    }
}

export const RNSVGSvg = getRNC('RNSVGSvgView');
export const RNSVGCircle = getRNC('RNSVGCircle');
export const RNSVGClipPath = getRNC('RNSVGClipPath');
