package com.kwai.kds.pulltorefresh;

import androidx.annotation.Nullable;

import com.facebook.react.LazyReactPackage;
import com.facebook.react.bridge.ModuleSpec;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.module.model.ReactModuleInfoProvider;
import com.kwai.kds.pulltorefresh.refresh.view.RefreshHeadViewManager;
import com.kwai.kds.pulltorefresh.refresh.view.RefreshViewManager;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.List;

/**
 * 作者：请叫我百米冲刺 on 2018/1/2 上午11:49
 * 邮箱：mail@hezhilin.cc
 */

public class RefreshReactPackage extends LazyReactPackage {

  @Override
  protected List<ModuleSpec> getNativeModules(ReactApplicationContext reactContext) {
    return Collections.emptyList();
  }

  @Override
  public ReactModuleInfoProvider getReactModuleInfoProvider() {
    return Collections::emptyMap;
  }

  @Override
  public List<ModuleSpec> getViewManagers(ReactApplicationContext reactContext) {
    ArrayList<ModuleSpec> moduleSpecs = new ArrayList<>();
    moduleSpecs.add(ModuleSpec.viewManagerSpec(RefreshViewManager::new, RefreshViewManager.NAME));
    moduleSpecs.add(ModuleSpec.viewManagerSpec(RefreshHeadViewManager::new, RefreshHeadViewManager.NAME));
    return moduleSpecs;
  }

  @Nullable
  @Override
  public List<String> getViewManagerNames(ReactApplicationContext reactContext) {
    return Arrays.asList(RefreshViewManager.NAME, RefreshHeadViewManager.NAME);
  }
}
