import { PixelRatio } from 'react-native';
export const isDownGesture = (x, y) => {
    return y > 0 && y > Math.abs(x);
};
export const dip2px = (dpValue) => {
    return dpValue * PixelRatio.get();
};
export const px2dip = (dpValue) => {
    return dpValue / PixelRatio.get();
};
export function steps(number, position = 'end') {
    if (isNaN(number) || number < 1) {
        number = 1;
    }
    let idx = 0;
    let func = Math.floor;
    let fix = 0;
    if (position === 'start' || position === 'jump-start') {
        func = Math.ceil;
    }
    else if (position === 'jump-none') {
        idx = 1;
    }
    else if (position === 'jump-both') {
        idx = -1;
        fix = 1;
    }
    return (x) => {
        return func(x * (number + idx)) / number + fix / number;
    };
}
