import React from 'react';
import {
  requireNativeComponent,
  StyleProp,
  ViewStyle,
  NativeSyntheticEvent,
} from 'react-native';

interface AndroidRefreshHeaderProps {
  viewHeight?: number;
  style?: StyleProp<ViewStyle>;
  onPushingState?: (
    event: NativeSyntheticEvent<{moveHeight: number; state: number}>
  ) => void;
}
const RefreshHeader = requireNativeComponent('RCTRefreshHeader');

const AndroidRefreshHeader: React.FC<AndroidRefreshHeaderProps> = (props) => {
  const {viewHeight, ...restProps} = props;
  // @ts-ignore
  return <RefreshHeader {...restProps} viewHeight={viewHeight} />;
};

AndroidRefreshHeader.defaultProps = {
  viewHeight: 0,
};

export default AndroidRefreshHeader;
