import React, {Component} from 'react';
import {Image} from 'react-native';

export enum LoadingSize {
  medium = 40,
  small = 25,
}

interface KyLoadingProps {
  size?: LoadingSize;
}
export class KyLoading extends Component<KyLoadingProps> {
  constructor(props: KyLoadingProps) {
    super(props);
  }
  render() {
    const {size = LoadingSize.small} = this.props;
    return (
      <Image
        style={{
          width: size,
          height: size,
        }}
        resizeMode={'cover'}
        source={require('../../../assets/loading.gif')}
      />
    );
  }
}
