import {PixelRatio} from 'react-native';

export const isDownGesture = (x: number, y: number) => {
  return y > 0 && y > Math.abs(x);
};
export const dip2px = (dpValue: number) => {
  return dpValue * PixelRatio.get();
};

export const px2dip = (dpValue: number) => {
  return dpValue / PixelRatio.get();
};

export function steps(number: number, position = 'end') {
  if (isNaN(number) || number < 1) {
    number = 1;
  }
  let idx = 0;
  let func = Math.floor;
  let fix = 0;
  if (position === 'start' || position === 'jump-start') {
    func = Math.ceil;
  } else if (position === 'jump-none') {
    idx = 1;
  } else if (position === 'jump-both') {
    idx = -1;
    fix = 1;
  }
  return (x: number): number => {
    return func(x * (number + idx)) / number + fix / number;
  };
}
