import React from 'react';
import { Text, View, Platform, StyleSheet } from 'react-native';
import { getKidColor, rem } from '../../utils';
export const headerHeight = rem(18);

interface CitySelectorHeaderProps {
    title: string;
}

export default function CitySelectorHeader(props: CitySelectorHeaderProps) {
    return (
        <View style={style.item}>
            <Text style={style.title}>{props.title}</Text>
        </View>
    );
}

const style = StyleSheet.create({
    item: {
        width: '100%',
        height: headerHeight,
        justifyContent: 'center',
    },
    title: {
        fontSize: rem(14),
        fontWeight: Platform.OS === 'ios' ? '400' : 'normal',
        color: getKidColor('cs_common_text_primary'),
        fontFamily: 'PingFangSC-Regular',
    },
});
