import React from 'react';
import { Host } from 'react-native-portalize';
import { RootTagContext } from './context';
import { DragAndDropRoot } from './DragAndDropRoot';

export type IProps = {
  rootTag?: number;
};

export const DragHost: React.FC<IProps> = (props) => {
  const host = (
    <DragAndDropRoot>
      <Host>{props.children}</Host>
    </DragAndDropRoot>
  );
  return props.rootTag ? (
    <RootTagContext.Provider value={props.rootTag}>{host}</RootTagContext.Provider>
  ) : (
    host
  );
};
