import React from 'react';
import type { LayoutRectangle } from 'react-native';
import type { IDnDSubject } from './DragAndDropRoot';

export type ContextType = string | symbol;

export const DragAndDropRootContext = React.createContext(
  {} as {
    poiMap: Record<ContextType, LayoutRectangle>;
    subjects: Record<ContextType, IDnDSubject>;
    initContext: (context: ContextType) => void;
  },
);

export const DropAreaContext = React.createContext<ContextType | ContextType[]>('drop-area');

export const RootTagContext = React.createContext<number>(0);
