import type { IDraggableProps } from './Draggable';
import Draggable from './Draggable';
import type { ReactNode } from 'react';
import React from 'react';
import type { ContextType } from './context';
import { DropAreaContext } from './context';
import type { IDropAreaProps } from './DropArea';
import { DropArea } from './DropArea';

export const createDraggableElement = (
  comp: JSX.Element | Element | ReactNode,
  key: ContextType | ContextType[],
  props: IDraggableProps,
) => {
  return (
    <DropAreaContext.Provider value={key}>
      <Draggable {...props}>{comp}</Draggable>
    </DropAreaContext.Provider>
  );
};
export const createDropElement = (
  comp: JSX.Element | Element | ReactNode,
  key: ContextType,
  props: IDropAreaProps,
) => {
  return (
    <DropAreaContext.Provider value={key}>
      <DropArea {...props}>{comp}</DropArea>
    </DropAreaContext.Provider>
  );
};
