import type { PullRefreshProps } from '@kds/refresh-list';
import PullRefresh from '@kds/refresh-list';
import type PullRefreshType from '@kds/refresh-list/dist/pull-refresh.android';
import React, { useMemo, forwardRef } from 'react';
import type { LoadingType } from '../loading';
import getKsRefreshControl from './refresh-control';

export type KidPullRefreshProps = {
  type?: LoadingType | string;
} & PullRefreshProps;
export const KidPullRefresh = forwardRef<PullRefreshType, KidPullRefreshProps>((props, ref) => {
  const { type, ...rest } = props;
  const RefreshControl = useMemo(() => getKsRefreshControl(type), [type]);

  return (
    <PullRefresh
      ref={ref}
      pullAnimHeight={70}
      RefreshControl={RefreshControl}
      {...rest}
    ></PullRefresh>
  );
});
