import React, { Component } from 'react';
import type { RefreshControlParams } from '@kds/refresh-list';
import { PullState } from '@kds/refresh-list';
import { KidLoading, LoadingType } from '../loading';

type KsRefreshControlProps = {};
type KsRefreshControlPropsState = {
  animated: boolean;
  percent: number;
};
export default function getKsRefreshControl(type: LoadingType | string = LoadingType.highlight) {
  return class KsRefreshControl extends Component<
    KsRefreshControlProps,
    KsRefreshControlPropsState
  > {
    constructor(props: KsRefreshControlProps) {
      super(props);
      this.state = {
        animated: false,
        percent: 0,
      };
    }
    setRefreshControlParams(refreshControlParams: RefreshControlParams) {
      if (refreshControlParams.pullState === PullState.PULLING) {
        this.setState({
          animated: false,
          percent: (Math.max(refreshControlParams.movePercent - 50, 0) / (100 - 50)) * 100,
        });
      } else if (refreshControlParams.pullState === PullState.LOADING) {
        this.setState({
          animated: true,
        });
      } else if (refreshControlParams.pullState === PullState.HOLDING) {
        this.setState({
          animated: true,
        });
      }
    }
    render() {
      const { animated, percent } = this.state;
      return <KidLoading type={type} animated={animated} percent={percent} />;
    }
  };
}
