import React from 'react';
import { render, fireEvent } from '@testing-library/react-native';
import { SpectrumView } from '../index';

describe('test SpectrumView', () => {
  it('props: all', () => {
    const { toJSON } = render(
      <SpectrumView
        width={100}
        height={100}
        barNum={4}
        barWidth={20}
        barColor='red'
        sleepMillis={1000}
        radius={10}
        duration={1000}
        style={{margin: 10}}
      />
    );

    expect(toJSON()).toMatchSnapshot();
  })

  it('props: required', () => {
    const { toJSON } = render(
      <SpectrumView
        width={100}
        height={100}
      />
    );

    expect(toJSON()).toMatchSnapshot();
  })
})