import React from 'react';
import { View, StyleProp, ViewStyle } from 'react-native';
import BaseStepper, { StepperChangeType, StepperContext } from './base-stepper';
import type { StepperProps as BaseStepperProps } from './base-stepper';

interface StepperProps extends Omit<BaseStepperProps, 'children'> {
  containerStyle?: StyleProp<ViewStyle>;
}

const Stepper: React.FC<StepperProps> = (props: StepperProps) => {
  return (
    <BaseStepper {...props}>
      <View style={[{flexDirection: 'row', justifyContent: 'center', alignItems: 'center', paddingHorizontal: 3.33}, props.containerStyle]}>
        <BaseStepper.decrease/>
        <BaseStepper.content/>
        <BaseStepper.increase/>
      </View>
    </BaseStepper>
  )
}

export default Stepper;
export { BaseStepper, Stepper, StepperChangeType, StepperContext};
export type { BaseStepperProps, StepperProps};