import {
  TextInputProps as RNTextInputProps,
  StyleProp,
  ViewStyle,
  TextStyle
} from 'react-native';

interface TextInputProps
  extends Omit<RNTextInputProps, 'onChange' | 'onFocus' | 'onBlur'> { }

interface TextareaStyleProps {
  containerStyle?: StyleProp<ViewStyle>;
  countStyle?: StyleProp<ViewStyle>;
  countLeftStyle?: StyleProp<TextStyle>;
  countRightStyle?: StyleProp<TextStyle>;
}

interface TextareaProps extends TextInputProps, TextareaStyleProps {
  defaultValue?: string; // 默认值
  value?: string; // 值
  placeholder?: string; // 占位
  disabled?: boolean; // 是否禁用
  editable?: boolean; // 是否可编辑
  rows?: number; // 行数
  autoHeight?: boolean; // 是否自适应高度
  count?: number; // 计数
  extra?: React.ReactNode;
  renderCount?: (inputCount: number, count: number) => React.ReactNode;
  onChange?: (value: string) => void;
}

export { TextareaProps };
