import * as chalk from 'chalk';
import cli from 'cli-ux';

export class Logger {
    static debug(...args: string[]): void {
        console.log(...args);
    }

    static loading(...args: string[]): void {
        cli.action.start(`[KRN-CLI]: ${args.join(' ')}`);
    }

    static stop(): void {
        cli.action.stop();
    }

    static log(...args: string[]): void {
        console.log(chalk.white(`[KRN-CLI]: ${args.join(' ')}`));
    }

    static success(...args: string[]): void {
        console.log(chalk.green(`[KRN-CLI]: ${args.join(' ')}`));
    }

    static warn(...args: string[]): void {
        console.log(chalk.yellow(`[KRN-CLI]: ${args.join(' ')}`));
    }

    static error(...args: string[]): void {
        console.log(chalk.red(`[KRN-CLI]: ${args.join(' ')}`));
    }
}
