export interface IBridgeResult {
    /**
     * 1 表示成功
     */
    result: number;
}
/**
 * KRN 容器启动参数
 */
export interface IInitAPPProps {
    rootTag: number;
    /**
     * bundle 包的唯一标识
     * android ✔ ios ✔️
     */
    bundleId: string;
    /**
     * 标识页面入口，一个 bundle 可以有多个入口
     * android ✔ ios ✔️
     */
    componentName: string;
    /**
     * 页面标题注意：Android 容器要显示 native 标题栏，scheme 里面的 title 参数不能为空
     * android ✔ ios ✔️
     */
    title?: string;
    /**
     * "1"：沉浸式状态栏，页面布局从屏幕坐标(0,0)开始
     * "0"或者不传:非沉浸式状态栏，页面布局从状态栏底部(0,20dp)开始布局
     * android ✔ ios ✔️
     */
    themeStyle?: '1' | '0';
    /**
     *  降级 H5 链接，RN 加载失败后，会读取这个值，执行走 H5 降级逻辑
     *  android ✔ ios ✔️
     */
    degradeWebUrl?: string;
    /**
     * 默认情况下，加载 RN 的 activity 必须实现 DefaultHardwareBackBtnHandler 接口，
     * 但是有一些弹窗、半屏不希望其所在的 activity 实现这个接口，所以我们加了这一个参数，直接传一个 flase 即可，默认是 true
     * android ✔ ios ❌
     */
    enableBackBtnHandler?: boolean;
    /**
     * 0：不显示容器加载 loading
     * 1：显示容器加载 loading
     * android ✔ ios ✔️
     */
    enableLoading?: 0 | 1;
    /**
     * 只加载 bundleVersionCode>=minBundleVersion 的 bundle；本地 bundle 不满足，则强制触发更新
     * android ✔ ios ✔️
     */
    minBundleVersion?: number;
    /**
     * 默认容器启动时的时间戳
     * android ✔ ios ❌
     */
    startTimestamp?: number;
    /**
     * 默认容器启动时的时间点(从手机启动时算)
     * android ✔ ios ❌
     */
    startTimeNodeSinceBoot?: number;
    /**
     * 默认容器创建时的时间戳
     * android ✔ ios ✔️
     */
    onCreateTimestamp: number;
    /**
     * 业务 bundle 加载时的时间戳
     * android ✔ ios ❌
     */
    onLoadBundleTimestamp?: number;
    /**
     * bundle 的版本号
     * android ✔ ios ❌
     */
    BundleVersionCode?: string;
    /**
     * 语言
     * android ✔ ios ❌
     */
    locale?: string;
}
