const shell = require('shelljs');
const path = require('path');

const PROJECT_ROOT = path.resolve(__dirname, '..');

const run = (cmd = '', opts) => {
    const res = shell.exec(cmd, {
        cwd: PROJECT_ROOT,
        ...opts,
    });

    if (res.code !== 0) {
        throw new Error(res);
    }

    return res;
};

exports.run = run;
