import { liveRnBridgeExecute } from '../utils/liveRnBridgeExecute';
import { IChangeLivePushNotificationServiceStatus } from './interface';

/**
 * 开播页设置开播通知推广 </br>
 * 支持版本: 9.0.20+ </br>
 * @param isServiceOn
 */
export function changeLivePushNotificationServiceStatus(
    params: IChangeLivePushNotificationServiceStatus,
): Promise<object> {
    return liveRnBridgeExecute(
        'changeLivePushNotificationServiceStatus',
        params,
    );
}

export type { IChangeLivePushNotificationServiceStatus };
