const initedKey = Object.keys(global);
const whiteList = [
    '__blobCollectorProvider',
    '__rootSiblingsInjected',
    'regjsgen',
    'evaluate',
    '$r',
    'system',
    'systemjs',
    '__cjsWrapper',
    'SystemJS',
    'System',
    'define',
    '__REDUX_DEVTOOLS_EXTENSION_COMPOSE__',
    'GetParams',
    'KRN_SERVER_IP',
];
initedKey.push(...whiteList);
setInterval(() => {
    const currentKey = Object.keys(global);
    for (const key of currentKey) {
        if (!initedKey.includes(key)) {
            console.warn(
                `[KRN-CLI]: 检测到有KDS禁止使用的全局变量\x1b[33m${key}\x1b[0m，可能引发不可预知问题，强烈建议去掉全局变量使用，如全局变量来自三方库，可联系KDS平台侧处理`,
            );
            initedKey.push(key);
        }
    }
}, 3000);
