import { idcardReg } from "../../src"

test('18位校验', () => {
    expect(idcardReg('111111111111111111')).toEqual({
        result: true,
        errMsg: '',
    })
})
test('18位校验', () => {
    expect(idcardReg('1111111111111111111')).toEqual({
        result: false,
        errMsg: '身份证号不合法',
    })
})
test('18位校验', () => {
    expect(idcardReg('11111111111111111')).toEqual({
        result: false,
        errMsg: '身份证号不合法',
    })
})
test('数字校验', () => {
    expect(idcardReg('abcdef111111111111')).toEqual({
        result: false,
        errMsg: '身份证号不合法',
    })
})
test('数字校验', () => {
    expect(idcardReg('11111111111111111x')).toEqual({
        result: true,
        errMsg: '',
    })
})
test('数字校验', () => {
    expect(idcardReg('11111111111111111X')).toEqual({
        result: true,
        errMsg: '',
    })
})
test('数字校验', () => {
    expect(idcardReg('x11111111111111111')).toEqual({
        result: false,
        errMsg: '身份证号不合法',
    })
})
test('数字校验', () => {
    expect(idcardReg('111111111X11111111')).toEqual({
        result: false,
        errMsg: '身份证号不合法',
    })
})
test('边界校验', () => {
    expect(idcardReg(' 111111111111111111 ')).toEqual({
        result: true,
        errMsg: '',
    })
})
expect(idcardReg(' ')).toEqual({
    result: false,
    errMsg: '请填写身份证号',
})
