/**
 * 格式化销量
 * 1. X <= 0，不显示该字段
 * 2. 0 < X < 10000时，展示「X」，X为整数
 * 3. X >= 10000时，展示「X.Y万+」，向下取整
 * @param { number } sales 
 * @returns { string }
 */
export function getSales(sales: number): string {
    try {
        const saleNum = Number(sales);
        if (saleNum <= 0) {
            return '';
        }
        if (saleNum >= 10000) {
            const result = '' + saleNum / 10000;
            if (result.indexOf('.') > -1) {
                return result.substring(0, result.indexOf('.') + 2) + '万+';
            }
            return Number(result).toFixed(1) + '万';
        }
        return saleNum.toFixed(0) + '';
    } catch (e) {
        console.error(e);
        return '';
    }
}
