// 身份证号校验是否合法
// 前17位为数字，后一位为x、X或者数字
export interface idCardRegResult {
    result: boolean;
    errMsg: string;
}
const defaultResult = {
    result: false,
    errMsg: '请填写身份证号',
};
export function idcardReg(_idcard: string): idCardRegResult {
    try {
        const idcard = _idcard.trim();
        if (!idcard) {
            return defaultResult;
        }
        if (/^\d{17}[xX0-9]$/.test(idcard)) {
            return {
                result: true,
                errMsg: '',
            };
        }
        return {
            result: false,
            errMsg: '身份证号不合法',
        };
    } catch (e) {
        console.error(e);
        return defaultResult;
    }
}
