// 姓名是否符合规范的校验方法
// 支持汉字或字母，允许在汉字/字母中间出现空格，且数量不超过5个，其他字符不允许
export interface nameRegResult {
    result: boolean;
    errMsg: string;
}
const deafultResult = {
    result: false,
    errMsg: '仅支持填写中/英文姓名',
};
export function nameReg(_name: string): nameRegResult {
    try {
        const name = _name.trim();
        if (!name) {
            return {
                result: false,
                errMsg: '请填写联系姓名',
            };
        }
        if (name.length > 30) {
            return {
                result: false,
                errMsg: '姓名长度不能超过30个字符',
            };
        }
        if (
            /^([\u4e00-\u9fa5]\s{0,5})+$/.test(name) ||
            /^([[a-zA-Z]\s{0,5})+$/.test(name)
        ) {
            return {
                result: true,
                errMsg: '',
            };
        }
        return deafultResult;
    } catch (e) {
        console.error(e);
        return deafultResult;
    }
}
