// 手机号校验
export interface phoneRegResult {
    result: boolean;
    errMsg: string;
}
const defultResult = {
    result: false,
    errMsg: '请输入正确的手机号',
};
// 手机号：1开头，3，4，5，6，7，8，9其中一个为第二个数字，后面跟9个数字
export function phoneReg(_phone: string) {
    try {
        const phone = _phone.trim();
        if (!/^1[3456789]\d{9}$/.test(phone)) {
            return defultResult;
        }
        return {
            result: true,
            errMsg: '',
        };
    } catch (e) {
        console.error(e);
        return defultResult;
    }
}
