declare type Deregistration = () => void;
export declare function eventFactory(): {
    off(handler: () => void): void;
    on(handler: () => void): Deregistration;
    once(handler: () => void): Deregistration;
    emit(): void;
};
export declare function eventFactory<T>(): {
    off(handler: (event: T) => void): void;
    on(handler: (event: T) => void): Deregistration;
    once(handler: (event: T) => void): Deregistration;
    emit(event: T): void;
};
export {};
