import React from 'react'
import classnames from 'classnames'

type IKey = "commercial_ads_commodity_close_black" | "commercial_ads_feedback_camera_black" | "commercial_ads_feedback_extension_black" | "commercial_ads_feedback_left_black" | "commercial_ads_feedback_lower_black" | "commercial_ads_feedback_right_black" | "commercial_ads_feedback_success_green" | "commercial_fentiao_fans_price_grey" | "commercial_fentiao_feed_fanstop_white_xl" | "commercial_fentiao_fentiao_logo_black" | "commercial_fentiao_home_brand_black" | "commercial_fentiao_home_other_black" | "commercial_fentiao_home_related_black" | "commercial_fentiao_home_robot_black" | "commercial_fentiao_mine_fuwuhao_black" | "commercial_fentiao_mine_promote_black" | "commercial_fentiao_mine_shengyitong_black" | "commercial_fentiao_mine_videoclip_black" | "commercial_fentiao_mine_wallet_black" | "commercial_fentiao_orderform_crown_black" | "commercial_fentiao_orderform_refund_black" | "commercial_fentiao_robot_diagnosis_black" | "commercial_fentiao_robot_throw_black" | "commercial_fentiao_shop_precise_grey" | "commercial_fentiao_shop_robot_grey" | "commercial_fentiao_shop_shoplive_black" | "commercial_fentiao_shop_shopvideo_black" | "commercial_fentiao_tabbar_data_black" | "commercial_fentiao_tabbar_logo_black" | "commercial_general_data_drop_black" | "commercial_general_data_rise_black" | "commercial_general_mine_audit_black" | "commercial_jx_home_academy_black" | "commercial_jx_home_incentive_task_black" | "commercial_jx_home_search_order_black" | "commercial_jx_personal_official_black" | "commercial_jx_profit_recruitment_task_black" | "commercial_jx_tabbar_logo_black" | "commercial_lanv_service_html_analysis_20" | "commercial_niu_data_effect_green" | "commercial_niu_data_expend_blue" | "commercial_niu_home_broadcast_blue" | "commercial_niu_home_charge_black" | "commercial_niu_home_count_black" | "commercial_niu_home_fans_pink" | "commercial_niu_home_help_green" | "commercial_niu_home_invoice_black" | "commercial_niu_home_oher_orange"

export interface IProps extends Omit<React.HTMLProps<HTMLSpanElement>, 'size'> {
    icon: IKey
    darkMode?: Boolean
    size?: string | number
    color?: string
}

const iconStyle = (size?: string | number, color?: string, style?: any): any => {
    if (!size && !color) {
      return style
    }

    const res = {} as any

    if (size) {
      const s = typeof size === 'number' ? `${size}px` : size
      res.fontSize = s
    }

    if (color) {
      res.color = color
    }

    return Object.assign(res, style)
  }

const IconFont = ({
    className,
    icon,
    darkMode = true,
    size,
    color,
    style,
    ...props
}: IProps) => React.createElement(
    'span',
    {
        className: classnames(
            'iconfont-commercial',
            `icon-${icon}`,
            darkMode ? `icon-${icon}-dual` : '',
            className
        ),
        'aria-hidden': 'true',
        style: iconStyle(size, color,  style),
        ...props
    }
)

export default IconFont