import React from 'react'
import classnames from 'classnames'

type IKey = "feed_hashtag_moviegrade_16" | "feed_nasa_comment_base_public_28" | "feed_nasa_comment_change_16" | "feed_nasa_comment_location" | "feed_nasa_comment_search" | "feed_nasa_comment_seen" | "feed_nasa_rank_personalized_44" | "feed_negativefeedback_cache_28" | "feed_negativefeedback_collect_off_28" | "feed_negativefeedback_danmakuoff_28" | "feed_negativefeedback_danmakusetting_28" | "feed_negativefeedback_download_28" | "feed_negativefeedback_interest_28" | "feed_negativefeedback_invisible_28" | "feed_negativefeedback_morefriends_28" | "feed_negativefeedback_notinterested_28" | "feed_negativefeedback_videosetup_28" | "feed_popup_icon_personalized_96" | "feed_setting_personalized_24" | "feed_setting_privacysummary" | "feed_sider_cache_start_18" | "feed_sider_cache_stop_18" | "feed_teensmode_defaultimage_grey_22"

export interface IProps extends Omit<React.HTMLProps<HTMLSpanElement>, 'size'> {
    icon: IKey
    darkMode?: Boolean
    size?: string | number
    color?: string
}

const iconStyle = (size?: string | number, color?: string, style?: any): any => {
    if (!size && !color) {
      return style
    }

    const res = {} as any

    if (size) {
      const s = typeof size === 'number' ? `${size}px` : size
      res.fontSize = s
    }

    if (color) {
      res.color = color
    }

    return Object.assign(res, style)
  }

const IconFont = ({
    className,
    icon,
    darkMode = true,
    size,
    color,
    style,
    ...props
}: IProps) => React.createElement(
    'span',
    {
        className: classnames(
            'iconfont-feed',
            `icon-${icon}`,
            darkMode ? `icon-${icon}-dual` : '',
            className
        ),
        'aria-hidden': 'true',
        style: iconStyle(size, color,  style),
        ...props
    }
)

export default IconFont