/** 每个页面核心模块的css query */
export declare type PageMainElMap = {
    [routerPath: string]: string;
};
/** sdk传入的参数 */
export declare type PluginConfig = {
    pageMainElMap: PageMainElMap;
    isDebugger?: boolean;
    enableReportRpv?: boolean;
};
/** ReportedMetrics记录的指标格式 */
export declare type MetricItem = {
    [page: string]: number;
};
/** 记录已上报的核心指标 */
export declare type ReportedMetrics = {
    fmp: MetricItem;
    sfmp: MetricItem;
    ffp: MetricItem;
    sffp: MetricItem;
};
