/**
 * Weblog 启动脚本，作用
 * 1. 分级加载主文件和插件
 * 2. 主文件加载前 mock 埋点上报方法
 */
import type { Weblogger } from '../config/types';
import '../util/polyfill';
/**
 * 定义一个构造函数，返回一个 weblog 的代理对象
 * @param args
 */
declare function Weblog(...args: any[]): Weblogger;
declare namespace Weblog {
    var config: (key: "plugins" | "main", value: any) => void;
    var loadPlugins: () => void;
    var init: (initOptions: any[]) => void;
}
export default Weblog;
