import type { MinaWeblogInstance } from "../../types/index";
import { BasePlugin } from '../base';
import { LifecycleConfig as AppLifeCycleConfig } from './lifecycle-proxy/app';
interface Config {
    launchIntervalLimit?: number;
    backgroundWaitingLimit?: number;
    App?: AppLifeCycleConfig;
}
interface ReportMsg {
    [key: string]: number | string | boolean;
}
export default class MinaAutoPV extends BasePlugin {
    static key: string;
    private config;
    /**
     * 兼容写法，如果在 Weblog 初始化设置 plugins 时，不需要设置 weblog
     * @param weblog
     */
    constructor(weblog?: MinaWeblogInstance, localGlobal?: any, config?: Config);
    proxy(): void;
    getAppConfig(): {
        onLaunch: (() => void)[];
        onShow: ((param: WechatMiniprogram.App.LaunchShowOption) => void)[];
        onHide: (() => void)[];
    } | AppLifeCycleConfig;
    apply(weblog: MinaWeblogInstance): void;
    send(data: ReportMsg, type?: string, immediate?: boolean): void;
    destroy(): void;
}
export {};
