/**
 * @kds/tachikoma-lib 因为 kds 的包暂不支持单独的 ts 定义包，相关 ts 类型也无法直接引用，所以暂时先把相关 ts 定义直接拉出来定义。
 */
declare namespace Tachikoma {
    const env: AppEnvironment;
}
declare const enum Platform {
    iOS = "iOS",
    android = "Android"
}
/**
 * 容器全局环境变量
 */
declare interface AppEnvironment {
    /**
     * 版本支持：iOS 版本支持tachikoma >= 0.5.108
     * 版本支持：Android 版本支持tachikoma >= 0.8.50
     * 版本支持：主站 App 版本支持 >= 10.7.30
     * 业务自定义env参数
     */
    [propName: string]: any;
    platform: Platform;
    osVersion: number;
    appName: string;
    appVersion: string;
    engineVersion: string;
    deviceWidth: number;
    deviceHeight: number;
    /**
     * 同deviceWidth。该变量已被废弃
     * @deprecated
     */
    availableWidth: number;
    /**
     * iOS为deviceHeight-statusBarHeight，安卓同deviceHeight。该变量已被废弃
     * @deprecated 可通过deviceHeight和statusBarHeight计算
     */
    availableHeight: number;
    scale: number;
    /**
     * 版本支持：iOS 版本支持tachikoma >= 0.5.96
     * 版本支持：Android 版本支持tachikoma >= 0.8.27
     * 版本支持：主站 App 版本支持 >= 10.5.20
     * 状态栏高度
     */
    statusBarHeight: number;
    /**
     * 版本支持：iOS 版本支持tachikoma >= 0.5.103
     * 版本支持：Android 版本支持tachikoma >= 0.8.35
     * 版本支持：主站 App 版本支持 >= 10.6.20
     * appKpn，区分app是主站、极速版或者独立app
     */
    productName: string;
}
