/**
 * 获取一个 logger 对象
 * @param option
 * @returns
 */
export declare function getLogger(option: {
    radar_id: string;
    name?: string;
}): {
    /**
     * 设置配置项
     * @param option
     */
    configure(option: {
        radar_id: string;
        name: string;
    }): void;
    /**
     * 自定义日志
     * @param level
     * @param args
     */
    log(level: string, ...args: any[]): void;
    /**
     * info 日志
     * @param args
     */
    info(...args: any[]): void;
    /**
     * warn 日志
     * @param args
     */
    warn(...args: any[]): void;
    /**
     * error 信息
     * @param args
     */
    error(...args: any[]): void;
};
