import type { TimingPerfName, TimingPerf, TimingCustomOptions } from '../types/plugins/types';
import type { HttpWeblogInstance } from "../types/types/index";
import { BasePlugin } from '../types/plugins/base';
export default class TimingMonitor extends BasePlugin {
    static key: string;
    /**
     * 兼容写法，如果在 Weblog 初始化设置 plugins 时，不需要设置 weblog
     * @param weblog
     */
    constructor(weblog?: HttpWeblogInstance);
    apply(weblog: HttpWeblogInstance): void;
    load: () => void;
    destroy(): void;
    calculate(key: TimingPerfName, options?: TimingCustomOptions): number;
    mark(key: 'firstScreen' | 'whiteScreen', options?: TimingCustomOptions): void;
    collect(key?: TimingPerfName, options?: TimingCustomOptions): TimingPerf | undefined;
}
