import type { HttpWeblogInstance } from "../../types/index";
import { BasePlugin } from '../base';
export default class CrashMonitor extends BasePlugin {
    private eventName;
    static key: string;
    private options;
    private updateIntervalId;
    private historyMoniorInfo;
    monitorId: string;
    /**
     * 兼容写法，如果在 Weblog 初始化设置 plugins 时，不需要设置 weblog
     * @param weblog
     */
    constructor(weblog?: HttpWeblogInstance);
    apply(weblog: HttpWeblogInstance): void;
    destroy(): void;
    pickHistoryInfo(): void;
    checkHistoryCrash(data: any): void;
    /**
     * 开始定时更新快照
     */
    start(): void;
    /**
     * 更新监控快照
     * @param {*} data 外部追加额外的快照信息
     */
    update(): void;
    /**
     * 停止监控过程
     * 1.在页面正常卸载时自动调用
     * 2.需要手动卸载时手动调用
     */
    stop: () => void;
}
