import type { EventParams } from './event';
export declare type StatEvents = 'LAUNCH_EVENT' | 'HEART_BEAT_EVENT' | 'APP_USAGE_STAT_EVENT' | 'EXCEPTION_EVENT' | 'DEVICE_STAT_EVENT';
export declare type MinaEventParams = EventParams & {
    [StatEvent in StatEvents]: {
        eventOptions: {
            [key: string]: any;
        };
    };
};
/**
 * 支持的事件
 */
export declare type EventType = keyof MinaEventParams;
/**
 * 事件对应的参数
 */
export declare type EventOptions<T extends EventType> = MinaEventParams[T]['eventOptions'];
export declare type ImmediatelyEventOptions<T extends EventType> = MinaEventParams[T]['eventOptions'] & {
    callback?: (error: Error) => void;
};
export declare type ClickOption = ImmediatelyEventOptions<'CLICK'>;
