declare class Cookie {
    get cookie(): any;
    /**
     *
     * 设置 cookie value
     * @param {string} key 设置的key
     * @param {*} value 设置的值
     * @param {number} [expirationTime] 过期时间，超过后这个 key 会清空，value 为 null
     * @param {boolean} [sync] 是否同步设置cookie
     */
    set(key: any, value: any, expirationTime?: number, sync?: boolean): void;
    get(key: string): any;
    remove(key: string): any;
    toCookieString(): string;
}
declare const _default: Cookie;
export default _default;
