declare class Storage {
    cache: {
        [key: string]: any;
    };
    /**
     *
     * 获取 storage 完整信息
     * @param {*} key
     */
    getCompleteStorage(key: string): any;
    /**
     *
     * 获取 storage value
     * @param {*} key
     */
    get(key: string): any;
    /**
     *
     * 设置 storage value
     * @param {string} key 设置的key
     * @param {*} value 设置的值
     * @param {number} [expirationTime] 过期时间，超过后这个 key 会清空，value 为 null
     * @param {boolean} [sync] 是否同步设置storage
     */
    set(key: string, value: any, expirationTime?: string | number, sync?: boolean): boolean;
    /**
     *
     * 删除 storage
     * @param {string} key
     */
    remove(key: string): boolean;
    /**
     *
     * 清除 storage
     */
    clear(): boolean;
}
declare const _default: Storage;
export default _default;
