import { NativeModules } from 'react-native';

export default async function useIsTestChannel(): Promise<Boolean> {
    return await NativeModules.Kds.invoke('system', 'getAppInfo', '')
        .then((data) => {
            if (data.c === 'TEST' || data.c === 'BETA') {
                return true;
            } else {
                return false;
            }
        })
        .catch((e) => {
            throw e;
        });
}
