import React from 'react';
import useBoolean from '../../useBoolean';
import useMount from '..';
import { Button, Text, View } from 'react-native';

// 本demo中，TestComponent一开始不会被挂载
// 点击挂载按钮后，state的值为true，触发TestComponent的挂载逻辑，打印 Mounted
// 点击卸载按钮后，state的值为false，TestComponent不会被渲染，触发TestComponent的卸载
const TestComponent = () => {
    useMount(() => {
        console.log('Mounted');
    });
    return <Text>Hello World</Text>;
};

export default () => {
    const [state, { toggle }] = useBoolean(false);

    return (
        <View>
            <Button title={state ? '卸载' : '挂载'} onPress={toggle} />
            {state && <TestComponent />}
        </View>
    );
};
