import { useEffect } from 'react';
import useLatest from '../useLatest/index';

export default function useUnmount(func: () => void) {
    const fnRef = useLatest(func);

    useEffect(
        () => () => {
            fnRef.current();
        },
        // eslint-disable-next-line react-hooks/exhaustive-deps
        [],
    );
}
