import { CommonRequestParams, TParams } from '@locallife/base-request';

export interface IRequestParam {
    url: string; // 请求path
    method?: RequestMethodType;
    queryParams?: TParams;
    params?: TParams; // 和接口强绑定
    headers?: TParams;
    businessName?: string;
    responseType?: string;
    timeout?: number; // 请求超时，单位 s
    commonRequestParams?: CommonRequestParams;
}

export enum RequestMethod {
    GET = 'GET',
    POST = 'POST',
}

type RequestMethodType = RequestMethod.GET | RequestMethod.POST;
